function emoji_replacer(str, p1) {
	var p = ord(p1.toString(16))
	if (p > 0) {
		var res = decimalToHex(p).toString().toUpperCase()
		if (p > 8252)
			return res.replace(/^([\da-f]+)$/i, '<img src="/opt/waxmppplugin/bin/wazapp/UI/common/images/emoji/20/$1.png" />');
		else
			return p1
	} else {
		return ''
	}
}

